  };

  return (
    <div>
      <input multiple type="file" onChange={onFileChange} />
      <button onClick={onUpload} style={{ marginLeft: 10, padding: '5px 10px' }}>
        Upload
      </button>
      <div style={{ marginTop: 10 }}>
        {status && <strong>{status}</strong>}
        {files.length > 0 && (
          <ul>
            {files.map((f, i) => (
              <li key={i}>{f.name}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
